import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [tumorProbability, setTumorProbability] = useState(null);
  const [progress, setProgress] = useState(0); // Progress state

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
    setProcessedImage(null);
    setTumorProbability(null);
    setProgress(0);
  };

  const sendRequest = async () => {
    if (!image) {
      alert("Please upload an image first!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      setProgress(20); // Simulating progress
      const response = await axios.post("http://localhost:5000/analyze", formData, {
        onUploadProgress: (progressEvent) => {
          setProgress(Math.min(80, (progressEvent.loaded / progressEvent.total) * 100)); // Update upload progress
        },
      });
      const data = response.data;

      if (data.heatmap_overlay) {
        setProcessedImage(`data:image/png;base64,${data.heatmap_overlay}`);
      }
      setTumorProbability(data.tumor_probability);
      setProgress(100); // Set progress to 100 after completion
    } catch (error) {
      console.error(error);
      alert("Error processing the request.");
      setProgress(0); // Reset progress on error
    }
  };

  return (
      <div className="app-container">
        <div className="upload-container">
          <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: "none" }}
              id="file-upload"
          />
          <label htmlFor="file-upload" className="upload-button">
            ADD IMAGE
          </label>
        </div>

        <div className="image-container">
          <div className="original-container">
            <h3>Original Image</h3>
            <div className="placeholder">
              {image ? <img src={URL.createObjectURL(image)} alt="Uploaded" className="uploaded-image" /> : "+"}
            </div>
          </div>
          <div className="processed-container">
            <h3>Processed Image</h3>
            <div className="processed-placeholder">
              {processedImage ? <img src={processedImage} alt="Processed" /> : "Processed image will appear here"}
            </div>
          </div>
        </div>

        <div className="controls-container">
          <button className="control-button" onClick={sendRequest}>
            ANALYZE
          </button>
        </div>

        {/* Progress Bar */}
        {progress > 0 && (
            <div className="progress-bar-container">
              <div className="progress-bar" style={{ width: `${progress}%` }}></div>
            </div>
        )}

        {/* Heatmap Legend */}
        <div className="legend-container">
          <h3>Heatmap Legend</h3>
          <div className="legend-gradient">
            <span>0%</span>
            <span>50%</span>
            <span>100%</span>
          </div>
        </div>

        <div className="results-container">
          <h3>Results</h3>
          {tumorProbability !== null ? (
              <div className="result-text">Tumor Probability: {tumorProbability.toFixed(4)}</div>
          ) : (
              <div className="result-text">No results yet</div>
          )}
        </div>
      </div>
  );
}

export default App;
