import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [image, setImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [modelStatus, setModelStatus] = useState("not_ready");

  // Sprawdź status modelu przy pierwszym renderze
  useEffect(() => {
    const checkBackendStatus = async () => {
      try {
        const response = await axios.get("http://localhost:5000/backend-status");
        if (response.data.status === "ready") {
          setModelStatus("ready");
        }
      } catch (error) {
        setModelStatus("not_ready");
      }
    };

    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 3000); // Sprawdzaj co 3 sekundy
    return () => clearInterval(interval); // Wyczyszczenie interwału
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setProcessedImage(null);
  };

  const handleAnalyze = async () => {
    if (modelStatus !== "ready") {
      alert("Backend is not ready yet. Please wait...");
      return;
    }

    if (!image) {
      alert("Please upload an image first!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      const response = await axios.post("http://localhost:5000/analyze", formData);
      const data = response.data;

      if (data.heatmap_overlay) {
        setProcessedImage(`data:image/png;base64,${data.heatmap_overlay}`);
      }
    } catch (error) {
      console.error(error);
      alert("Error processing the request.");
    }
  };

  return (
      <div>
        <h1>Status Backend: {modelStatus === "ready" ? "Ready" : "Not Ready"}</h1>
        <div>
          <input type="file" accept="image/*" onChange={handleImageUpload} />
          <button onClick={handleAnalyze}>Analyze</button>
        </div>
        {processedImage && <img src={processedImage} alt="Processed Heatmap" />}
      </div>
  );
}

export default App;
