import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [tumorProbabilityMax, setTumorProbabilityMax] = useState(null);
  const [tumorProbabilityMean, setTumorProbabilityMean] = useState(null);
  const [tumorHotArea, setTumorHotArea] = useState(null);
  const [progress, setProgress] = useState(0);
  const [modelReady, setModelReady] = useState(false);

  useEffect(() => {
    const checkBackendStatus = async () => {
      try {
        const response = await axios.get("http://localhost:5000/backend-status");
        setModelReady(response.data.backend_ready);
      } catch (error) {
        console.error("Error checking backend status:", error);
        setModelReady(false);
      }
    };

    // Sprawdź status backendu co 2 sekundy
    const interval = setInterval(checkBackendStatus, 2000);
    checkBackendStatus(); // Sprawdź od razu przy załadowaniu strony

    return () => clearInterval(interval); // Wyczyść interval przy odmontowaniu komponentu
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setProcessedImage(null);
    setTumorProbabilityMax(null);
    setTumorProbabilityMean(null);
    setTumorHotArea(null);
    setProgress(0);
  };

  const trackProgress = () => {
    const eventSource = new EventSource("http://localhost:5000/progress");
    eventSource.onmessage = (event) => {
      const progressValue = Math.min(parseInt(event.data, 10), 100);
      setProgress(progressValue);

      if (progressValue >= 100) {
        eventSource.close();
      }
    };

    eventSource.onerror = () => {
      console.error("Error with SSE connection.");
      eventSource.close();
    };
  };

  const sendRequest = async () => {
    if (!modelReady) {
      alert("Backend is not ready yet. Please wait...");
      return;
    }

    if (!image) {
      alert("Please upload an image first!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      trackProgress(); // Rozpocznij nasłuchiwanie progresu

      const response = await axios.post("http://localhost:5000/analyze", formData);
      const data = response.data;

      if (data.heatmap_overlay) {
        setProcessedImage(`data:image/png;base64,${data.heatmap_overlay}`);
      }

      setTumorProbabilityMax(data.tumor_probability_max || null);
      setTumorProbabilityMean(data.tumor_probability_mean || null);
      setTumorHotArea(data.tumor_hot_area || null);
    } catch (error) {
      console.error(error);
      alert("Error processing the request.");
    }
  };

  return (
      <div className="app-container">
        <div className="upload-container">
          <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: "none" }}
              id="file-upload"
          />
          <label htmlFor="file-upload" className="upload-button">
            ADD IMAGE
          </label>
        </div>

        <p className="model-status">
          {modelReady ? "Backend Ready" : "Backend Initializing... Please wait."}
        </p>

        <div className="image-container">
          <div className="original-container">
            <h3>Original Image</h3>
            <div className="placeholder">
              {image ? <img src={URL.createObjectURL(image)} alt="Uploaded" className="uploaded-image" /> : "+"}
            </div>
          </div>
          <div className="processed-container">
            <h3>Processed Image</h3>
            <div className="processed-placeholder">
              {processedImage ? (
                  <img src={processedImage} alt="Heatmap" className="uploaded-image" />
              ) : (
                  "Processed heatmap will appear here"
              )}
            </div>
          </div>
        </div>

        <div className="controls-container">
          <button className="control-button" onClick={sendRequest}>
            ANALYZE
          </button>
          <div className="progress-container">
            <div className="progress-bar">
              <div className="progress" style={{ width: `${progress}%` }}></div>
            </div>
            <p>{progress}% analyzed</p>
          </div>
        </div>

        <div className="results-container">
          <h3>Results</h3>
          {tumorProbabilityMax !== null && tumorProbabilityMean !== null && tumorHotArea !== null ? (
              <div>
                <div className="result-text">Max Probability: {tumorProbabilityMax.toFixed(4)}</div>
                <div className="result-text">Mean Probability: {tumorProbabilityMean.toFixed(4)}</div>
                <div className="result-text">Hot Area Percentage: {(tumorHotArea * 100).toFixed(2)}%</div>
              </div>
          ) : (
              <div className="result-text">No results yet</div>
          )}
        </div>
      </div>
  );
}

export default App;
