import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [results, setResults] = useState("");

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
    setProcessedImage(null);
    setResults("");
  };

  const sendRequest = async (endpoint) => {
    if (!image) {
      alert("Please upload an image first!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      const response = await axios.post(`http://localhost:5000/${endpoint}`, formData);
      setResults(JSON.stringify(response.data, null, 2));
    } catch (error) {
      console.error(error);
      setResults("Error: Unable to process the request.");
    }
  };

  return (
      <div className="app-container">
        <div className="upload-container">
          <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: "none" }}
              id="file-upload"
          />
          <label htmlFor="file-upload" className="upload-button">
            ADD IMAGE
          </label>
        </div>

        <div className="image-container">
          <div className="placeholder">
            {image ? <img src={URL.createObjectURL(image)} alt="Uploaded" className="uploaded-image" /> : "+"}
          </div>
          <div className="processed-placeholder">
            {processedImage ? <img src={processedImage} alt="Processed" /> : "Processed image"}
          </div>
        </div>

        <div className="controls-container">
          <button className="control-button" onClick={() => sendRequest("analyze")}>
            ANALYZE
          </button>
          <button className="control-button" onClick={() => sendRequest("resolution")}>
            INCREASE RESOLUTION
          </button>
        </div>

        <div className="results-container">
          <h3>Results</h3>
          <pre>{results}</pre>
        </div>
      </div>
  );
}

export default App;
