import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [image, setImage] = useState(null);
  const [heatmapUrl, setHeatmapUrl] = useState(null); // Heatmap URL
  const [legendUrl, setLegendUrl] = useState(null); // Legend URL
  const [tumorProbability, setTumorProbability] = useState(null);
  const [progress, setProgress] = useState(0);

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
    setHeatmapUrl(null);
    setLegendUrl(null);
    setTumorProbability(null);
    setProgress(0);
  };

  const sendRequest = async () => {
    if (!image) {
      alert("Please upload an image first!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      const response = await axios.post("http://localhost:5000/analyze", formData, {
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
        },
      });
      const data = response.data;

      if (data.heatmap_url) {
        setHeatmapUrl(data.heatmap_url); // Heatmap URL
      }
      if (data.legend_url) {
        setLegendUrl(data.legend_url); // Legend URL
      }
      setTumorProbability(data.tumor_probability);
    } catch (error) {
      console.error(error);
      alert("Error processing the request.");
    }
  };

  return (
      <div className="app-container">
        <div className="upload-container">
          <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: "none" }}
              id="file-upload"
          />
          <label htmlFor="file-upload" className="upload-button">
            ADD IMAGE
          </label>
        </div>

        <div className="image-container">
          <div className="original-container">
            <h3>Original Image</h3>
            <div className="placeholder">
              {image ? <img src={URL.createObjectURL(image)} alt="Uploaded" className="uploaded-image" /> : "+"}
            </div>
          </div>
          <div className="processed-container">
            <h3>Processed Image</h3>
            <div className="processed-placeholder">
              {heatmapUrl ? (
                  <img src={heatmapUrl} alt="Heatmap" className="uploaded-image" />
              ) : (
                  "Processed heatmap will appear here"
              )}
            </div>
          </div>
        </div>

        <div className="controls-container">
          <button className="control-button" onClick={sendRequest}>
            ANALYZE
          </button>
        </div>

        {legendUrl && (
            <div className="legend-container">
              <h4>Heatmap Legend</h4>
              <img src={legendUrl} alt="Heatmap Legend" className="legend-image" />
            </div>
        )}

        {progress > 0 && progress < 100 && (
            <div className="progress-container">
              <div className="progress-bar">
                <div className="progress" style={{ width: `${progress}%` }} />
              </div>
            </div>
        )}

        <div className="results-container">
          <h3>Results</h3>
          {tumorProbability !== null ? (
              <div className="result-text">Tumor Probability: {tumorProbability.toFixed(4)}</div>
          ) : (
              <div className="result-text">No results yet</div>
          )}
        </div>
      </div>
  );
}

export default App;
